INSERT INTO `cms_channel` VALUES (1,0,1,'2,6',0,'新闻动态','http://baidu.com',2,1,1,'',0,'/news/','/news/{id}.html','','','','article',NULL,'2023-03-17 10:54:52',NULL),(2,1,1,'',1,'公司新闻','',1,1,1,'',0,'/news_company/','/news_company/{id}.html','','','','article',NULL,'2023-03-17 10:54:52',NULL),(4,0,4,'12,13,14,15,16',0,'产品展示','',3,1,1,'',0,'/product/','/product/{id}.html','','','','image',NULL,'2023-02-25 16:42:35',NULL),(5,2,1,'7',2,'12312312','',100,1,1,'',0,'','','','','','article',NULL,'2022-11-27 14:31:21','2022-12-05 20:37:12'),(6,1,1,'',1,'行业动态','',2,1,1,'',0,'/news_industry/','/news_industry/{id}.html','','','','article',NULL,'2023-01-08 20:04:16',NULL),(7,5,1,'',3,'11111','',100,1,1,'',0,'','','','','','article',NULL,'2022-11-27 14:31:40','2022-12-05 20:37:05'),(8,0,8,'',0,'公司简介','/upload/images/2023-01-30/cq57t3ixsaxwurndmg.jpeg',1,1,2,'',0,'/about/','','','','<p style=\"text-align: left;\">机械产品是指机械厂家向用户或市场所提供的成品或附件如汽车、发动机、机床等都称为机械产品.任何机械产品按传统的习惯都可以看作由若干部件组成部件又可分为不同层次的子部件（也称分部件或组件）直至最基本的零件单元。</p><p style=\"text-align: left;\">产品的生产过程是指把原材料变为成品的全过程。机械产品的生产过程一般包括：</p><p style=\"text-align: left;\">⑴生产与技术的准备如工艺设计和专用工艺装备的设计和制造、生产计划的编制、生产资料的准备等；</p><p style=\"text-align: left;\">⑵毛坯的制造如铸造、锻造、冲压等；</p><p style=\"text-align: left;\">⑶零件的加工切削加工、热处理、表面处理等；</p><p style=\"text-align: left;\">⑷产品的装配如总装、部装、调试检验和油漆等；</p><p style=\"text-align: left;\">⑸生产的服务如原材料、外购件和工具的供应、运输、保管等。</p><p style=\"text-align: left;\">零件的加工包括坯料的生产、以及对坯料进行各种机械加工、特种加工和热处理等，使其成为合格零件的过程。极少数零件加工采用精密铸造或精密锻造等无屑加工方法。通常毛坯的生产有铸造、锻造、焊接等；常用的机械加工方法有：钳工加工、车削加工、钻削加工、刨削加工、铣削加工、镗削加工、磨削加工、数控机床加工、拉削加工、研磨加工、珩磨加工等；常用的热处理方法有：正火、退火、回火、时效、调质、淬火等；特种加工有：电火花成型加工、电火花线切割加工、电解加工、激光加工、超声波加工等。只有根据零件的材料、结构、形状、尺寸、使用性能等，选用适当的加工方法，才能保证产品的质量，生产出合格零件。</p>','single_page',NULL,'2023-02-25 11:31:09',NULL),(9,0,9,'',0,'荣誉资质','',4,1,1,'',0,'/honor/','/honor/{id}.html','','','','image',NULL,'2023-01-03 10:25:12',NULL),(10,0,10,'',0,'在线留言','',5,1,2,'',0,'/guestbook/','','{module}/single_page/guestbook.html','','','single_page',NULL,'2023-01